var grammars = require('./grammars')

exports.secret_name = function(req, res){
    function random_choice(arr){ 
        return  arr[Math.floor(Math.random() * arr.length)] }
    output = { 
        result: 'success', 
        secret_name: 
            random_choice(grammars.adjectives)+' '+random_choice(grammars.animals)
        } 
    res.json(output)
}

exports.add_party = function(req, res, next, db){
    if(req.query.secret_name)
        db.get('parties').insert({"secret_name": req.query.secret_name})
        res.send("good")
}